import React, { useState, useEffect } from "react";
import "../style/PccQuestionaire.css";
import Table from "./Table";
import Loader from "./Loader";

function PccQuestionaire() {
  const [person, setPerson] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  useEffect(async () => {
    const response = await fetch("http://localhost:8000/api/v1/pcc/");
    const res = await response.json();
    setData(res);
    setLoading(false);
  });
  return (
    <div className="questionaire">
      <label htmlFor="cars">Select Process Area:</label>

      <select name="cars" id="cars">
        <option value="">Select</option>
        <option value="volvo">SYS. 1</option>
        <option value="saab">SYS. 2</option>
        <option value="mercedes">SYS. 3</option>
        <option value="audi">SYS. 4</option>
      </select>
      <div
        className="table"
        style={loading ? { border: "1px solid #ddd" } : {}}
      >
        {loading ? <Loader /> : <Table data={data} />}
      </div>
    </div>
  );
}

export default PccQuestionaire;
