import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import LocationOnIcon from "@material-ui/icons/LocationOn";
import SearchIcon from "@material-ui/icons/Search";
import "./App.css";
function outline() {
  var searchBorder = document.getElementById("search");
  var searchBox = document.getElementById("searchBox");
  searchBorder.style.borderColor = "orange";
  searchBorder.style.borderWidth = "3px";
  searchBox.style.borderColor = "transparent";
}
function noline() {
  var searchBorder = document.getElementById("search");
  var searchBox = document.getElementById("searchBox");
  searchBorder.style.borderColor = "transparent";
  searchBox.style.borderColor = "#b6b6b6";
}
function App() {
  return (
    <div className="App">
      <header className="App-header">
        <div className="header-container">
          <div class="inner-header flex">
            <div className="logo font-white bold">Borgaon</div>
            <div className="address font-white flex bold vcenter">
              <LocationOnIcon className="cartIcon" />
              <div>Select your address</div>
            </div>
            <div className="search-bar" id="search">
              <form>
                <input
                  type="text"
                  id="searchBox"
                  class="searchbar padding-around"
                  placeholder="Search for products, shops and more..."
                  onFocus={outline}
                  onBlur={noline}
                />
                <button type="submit" class="searchbutton">
                  <SearchIcon className="searchIcon" />
                </button>
              </form>
            </div>
            <div className="login-greet vcenter bold font-white padding-around">
              Sign In
            </div>
            <div className="orders vcenter font-white bold padding-around">
              My Orders
            </div>
            <div className="shopping-cart vcenter flex bold font-white padding-around">
              <ShoppingCartIcon className="cartIcon" />
              <div className="cart-text">cart</div>
            </div>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
