import React, { useState, useEffect } from "react";
import "../style/PccQuestionaire.css";
import Table from "./Table";
import Loader from "./Loader";

function PccQuestionaire() {
  const [person, setPerson] = useState(null);

  const [data, setData] = useState([]);
  const uniqueTags = [];

  const [loading, setLoading] = useState(true);
  useEffect(async () => {
    const response = await fetch("http://localhost:8000/api/v1/pcc/");
    const res = await response.json();
    res.map((data) => {
      if (uniqueTags.indexOf(data.nationality) === -1) {
        uniqueTags.push(data.nationality);
      }
    });
    setData(res);
    setLoading(false);
  });
  return (
    <div className="questionaire">
      <label htmlFor="cars">Select Process Area:</label>

      <select name="cars" id="cars">
        <option value="">Select</option>
        <option>{uniqueTags.length}</option>
      </select>
      <div
        className="table"
        style={loading ? { border: "1px solid #ddd" } : {}}
      >
        {loading ? <Loader /> : <Table data={data} />}
      </div>
    </div>
  );
}

export default PccQuestionaire;
